(function() {
    var validate = require('validate'),
        assert = require('assert');

    try {
        console.log('Testing validate.isEmpty()');
        assert.strictEqual(validate.isEmpty(false), false, 'validate.isEmpty(false) returned true.');
        assert.strictEqual(validate.isEmpty(0), false, 'validate.isEmpty(0) returned true.');
        assert.strictEqual(validate.isEmpty(''), false, 'validate.isEmpty("") returned true.');
        assert.strictEqual(validate.isEmpty(null), false, 'validate.isEmpty(null) returned true.');
        assert.strictEqual(validate.isEmpty(undefined), false, 'validate.isEmpty(undefined) returned true.');
        assert.strictEqual(validate.isEmpty(NaN), false, 'validate.isEmpty(NaN) returned true.');
        assert.strictEqual(validate.isEmpty({}), true, 'validate.isEmpty({}) returned false.');
        assert.strictEqual(validate.isEmpty([]), true, 'validate.isEmpty([]) returned false.');
        assert.strictEqual(validate.isEmpty(function(){}), false, 'validate.isEmpty(function(){}) returned true.');
        assert.strictEqual(validate.isEmpty([1,2,3]), false, 'validate.isEmpty([1,2,3]) returned true.');
        assert.strictEqual(validate.isEmpty({'key': 'value'}), false, 'validate.isEmpty({"key": "value"}) returned true.');
        assert.strictEqual(validate.isEmpty(new Date(79,5,24,11,33,0)), true, 'validate.isEmpty(new Date(79,5,24,11,33,0)) returned false.');
        assert.strictEqual(validate.isEmpty(), false, 'validate.isEmpty() returned true.');
        console.log('Everything passed.');
    } catch(e) {
        console.log(e);
    }
})();
