/**
 * Module dependencies.
 */
var common = require('./index.js'),
    hapi = common.hapi;

var api =
    hapi('Hapi API')
        .meta({
            'Host': 'api.hapi.co',
            'Accept': 'multipart/nav-data'
        })
        .req('entry', 'GET', '/')
        .req('get-users', 'GET', '/users')
        .req('del-user', 'DELETE', '/users/{user}')
        .req('put-user', 'PUT', '/users/{user}')
        .meta({
            'Content-Type': 'application/vnd.hapi.co.user+json'
        })
        .input({
            '_id': 'first_name'
        })
        .input({
            '_id': 'last_name'
        })
        .req('get-merchants', 'GET', '/merchants')
        .meta({
            'Accept-Charset': 'utf-8'
        })
        .req('get-merchant', 'GET', '/merchants/{merchant}')
        .meta({
            'Accept-Charset': 'utf-8'
        })
        .req('get-widgets', 'GET', '/widgets')
        .meta({
            'Accept-Charset': 'utf-8'
        })
        .req('get-widget', 'GET', '/widgets/{widget}')
        .meta({
            'Accept-Charset': 'utf-8'
        })
        .req('get-widgets', 'GET', '/otherwidgets')
        .meta({
            'Accept-Charset': 'utf-8'
        })
        .req('twitter-entry', 'GET', '/')
        .meta({
            'Host': 'api.twitter.com',
            'Accept': 'multipart/nav-data'
        })
        .req('twitter-put-tweet', 'PUT', '/tweets/{tweet}')
        .meta({
            'Host': 'api.twitter.com',
            'Accept': 'multipart/nav-data',
            'Content-Type': 'application/vnd.twitter.com.tweet+json'
        })
        .input({
            '_id': 'text'
        })
        .res('entry', '200', ['entry', 'twitter-entry'])
        .res('get-users', '200', [
            'entry', 'del-user', 'put-user', 'twitter-entry'
        ]);

console.dir(api.respond('entry', '200'));
console.dir(api.respond('get-users', '200'));
